command.script:
$arrService = Get-Service -Name @option.service@

if ($arrService.Status -ne 'Stopped')
{
    Stop-Service -Name @option.service@
    write-host 'Stopping @option.service@'

    Start-Sleep -seconds 10

    $arrService.Refresh()

    if ($arrService.Status -eq 'Stopped')
    {
        Write-Host '@option.service@ is now Stopped'
    }
} else {
    write-host '@option.service@ is stopped, nothing to do.'
}
